def humanize(flow):
    n = 0
    while flow >= 1000:
        flow /= 1024
        n += 1
    if n == 2:
        return '{:.1f}'.format(flow) + 'M'
    else:
        return '{:.0f}'.format(flow) + ('B', 'K')[n]


def getdate():
    rec_list1 = []
    tra_list1 = []
    global rec_list0
    global tra_list0
    global time0
    dates = open('/proc/net/dev', 'r').readlines()[2:]
    time1 = time.time()
    for date in dates:
        rec_list1.append(int(date.split()[1]))
        tra_list1.append(int(date.split()[9]))

    receive = max(map(lambda x, y: y - x, rec_list0, rec_list1)) / (time1 - time0)
    transmit = max(map(lambda x, y: y - x, tra_list0, tra_list1)) / (time1 - time0)
    ###############################################################
    receive = gettime()+'\n'+'↓' + humanize(receive)+ '\n'  \
    			+'¤'+ str(psutil.cpu_percent(None)).split('.')[0] +'▣' 	\
    			+ str(psutil.virtual_memory().percent).split('.')[0]

    transmit = '↑ ' + humanize(transmit)
    rec_list0 = rec_list1.copy()
    tra_list0 = tra_list1.copy()
    time0 = time1
    return '{:<6} '.format(receive), '{:>6} '.format(transmit)

def gettime():
    return time.strftime('  %H:%M  ')

# 实时刷新流量显示
def refresh():
    if mode == 0:
        l.config(text=' '.join(getdate()), width=0)
    if mode == 1:
        l.config(text=getdate()[0], width=0)
    elif mode == 2:
        # l.config(text=''.join(getdate()), width=16)
        l.config(text=getdate()[0], width=0)
    root.after(second * 1000, refresh)


def move(e):
    if e.x_root < root.winfo_screenwidth() - 10:
        new_x = e.x - x + root.winfo_x()
        new_y = e.y - y + root.winfo_y()
        if new_x < 10:
            new_x = 0
        if new_x > root.winfo_screenwidth() - root.winfo_width() - 10:
            new_x = root.winfo_screenwidth() - 3
        if new_y < 10:
            new_y = 0
        if new_y > root.winfo_screenheight() - root.winfo_height() - 10:
            new_y = root.winfo_screenheight() - root.winfo_height()
        root.geometry('+{}+{}'.format(new_x, new_y))


def click(e):
    global x
    global y
    x = e.x
    y = e.y


def change_skin(e):
    global skins
    global skin
    if skin == len(skins) - 1:
        skin = 0
    else:
        skin += 1
    l.config(bg=skins[skin][0], fg=skins[skin][1])


def change_mode(e):
    global mode
    if mode == 2:
        mode = 0
    else:
        mode += 1


# 退出程序，并保存退出之前状态
def exit_program(e):
    global mode
    if mode == 0:
        mode = 2
    else:
        mode -= 1
    pos_x = root.winfo_x()
    pos_y = root.winfo_y()
    dic = {'pos_x': pos_x, 'pos_y': pos_y, 'mode': mode, 'skin': skin}
    fl = open(os.environ['HOME'] + '/.Flow.pkl', 'wb')
    pickle.dump(dic, fl)
    fl.close()
    exit()


# 取消隐藏
def show(e):
    if root.winfo_x() == root.winfo_screenwidth() - 3:
        root.geometry('+{}+{}'.format(root.winfo_screenwidth() - root.winfo_width() - 10, root.winfo_y()))


rec_list0 = []  # 初始数据
tra_list0 = []  # 初始数据
second = 1  # 间隔时间
dates = open('/proc/net/dev', 'r').readlines()[2:]
time0 = time.time()
for date in dates:
    rec_list0.append(int(date.split()[1]))
    tra_list0.append(int(date.split()[9]))

root = tk.Tk()
root.geometry('49x40'+'+{}+{}'.format(pos_x, pos_y))  # 窗口初始位置
root.overrideredirect(True)  # 去掉标题栏
root.wm_attributes('-topmost', 1)  # 置顶窗口
skins = [('black', 'Ivory'),('black', 'white'), ('black', 'white'), ('black', 'Ivory') ]
l = tk.Label(root, text='   Starting...   ', bg=skins[skin][0], fg=skins[skin][1])
l.pack()
x = 0
y = 0
l.bind('<Button-1>', click)
l.bind('<B1-Motion>', move)
l.bind('<Double-Button-3>', exit_program)
l.bind('<Double-Button-1>', change_skin)
l.bind('<Button-3>', change_mode)
l.bind('<Enter>', show)

root.after(second * 1000, refresh)
root.mainloop()
